import React from 'react';
import { BrowserRouter as Router, Route, Switch } from 'react-router-dom';
import HomePage from './pages/HomePage';
import SearchEvents from './components/SearchEvents'; // Importa il componente di ricerca
import { useTranslation } from 'react-i18next';
import './styles/App.css'; // Assicurati che il percorso sia corretto

function App() {
  const { t, i18n } = useTranslation();

  // Funzione per cambiare lingua
  const changeLanguage = (lng) => {
    i18n.changeLanguage(lng); // Cambia la lingua tra 'en' (inglese) e 'it' (italiano)
  };

  return (
    <Router>
      <div>
        {/* Bottoni per cambiare lingua */}
        <button onClick={() => changeLanguage('en')}>English</button>
        <button onClick={() => changeLanguage('it')}>Italiano</button>

        {/* Titolo della pagina */}
        <h1>{t('home')}</h1> {/* La chiave 'home' verr√† tradotta in base alla lingua selezionata */}

        {/* Configurazione del routing */}
        <Switch>
          <Route path="/" exact>
            <HomePage />
          </Route>
          <Route path="/search-events" component={SearchEvents} /> {/* Aggiungi la rotta di ricerca */}
          <Route path="/edit-event/:id" component={EditEvent} /> {/* Nuova rotta per la modifica */}
          <Route path="/register/:id" component={RegisterParticipant} /> {/* Nuova rotta per la registrazione */}
        </Switch>
      </div>
    </Router>
  );
}

export default App;
